// frontend/src/auth/Login.jsx
import React, { useState } from "react";
import {
  Box,
  Button,
  TextField,
  Typography,
  Paper,
  Alert,
  CircularProgress,
} from "@mui/material";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("tenant");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      const res = await axios.post("/api/auth/login", { email, password, role });
      localStorage.setItem("token", res.data.access_token);
      localStorage.setItem("user", JSON.stringify(res.data.user));
      navigate("/dashboard");
    } catch (err) {
      setError("Login failed: Invalid credentials");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box
      sx={{
        backgroundColor: "#121417",
        color: "#F3F4F6",
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        p: 2,
      }}
    >
      <Paper
        sx={{
          width: 360,
          p: 4,
          textAlign: "center",
          backgroundColor: "#1F2327",
          color: "#F3F4F6",
        }}
        elevation={6}
      >
        <img
          src="/logo.png"
          alt="Portfolio Pilot Logo"
          style={{ width: "160px", marginBottom: "1.5rem" }}
        />

        <Typography variant="h5" gutterBottom>
          Login
        </Typography>

        {error && (
          <Alert severity="error" sx={{ my: 2 }}>
            {error}
          </Alert>
        )}

        <form onSubmit={handleLogin}>
          <TextField
            label="Email"
            fullWidth
            type="email"
            required
            margin="normal"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            InputProps={{ style: { color: "#F3F4F6" } }}
            InputLabelProps={{ style: { color: "#9CA3AF" } }}
          />
          <TextField
            label="Password"
            type="password"
            fullWidth
            required
            margin="normal"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            InputProps={{ style: { color: "#F3F4F6" } }}
            InputLabelProps={{ style: { color: "#9CA3AF" } }}
          />

          <Box sx={{ mt: 2, display: "flex", justifyContent: "space-between" }}>
            <Button
              variant={role === "landlord" ? "contained" : "outlined"}
              color="primary"
              fullWidth
              sx={{ mr: 1 }}
              onClick={() => setRole("landlord")}
            >
              Landlord
            </Button>
            <Button
              variant={role === "tenant" ? "contained" : "outlined"}
              color="primary"
              fullWidth
              sx={{ ml: 1 }}
              onClick={() => setRole("tenant")}
            >
              Tenant
            </Button>
          </Box>

          <Button
            type="submit"
            fullWidth
            disabled={loading}
            variant="contained"
            sx={{ mt: 3, backgroundColor: "#3B82F6", "&:hover": { backgroundColor: "#2563EB" } }}
          >
            {loading ? <CircularProgress size={22} color="inherit" /> : "Log In"}
          </Button>
        </form>
      </Paper>
    </Box>
  );
}
